		{
			int idx = inv->getSignedIntParameter();
			
			DistributedObject* _m_res = getZone(idx);
			resp->insertLong(_m_res == NULL ? 0 : _m_res->_getObjectID());
		}
		break;
	case RPC_GETZONECOUNT__:
		{
			
			int _m_res = getZoneCount();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_GETMAXPLAYERS__:
		{
			
			int _m_res = getMaxPlayers();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_GETTOTALPLAYERS__:
		{
			
			int _m_res = getTotalPlayers();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_GETDELETEDPLAYERS__:
		{
			
			int _m_res = getDeletedPlayers();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_GETPLAYERMANAGER__:
		{
			
			DistributedObject* _m_res = getPlayerManager();
			resp->insertLong(_m_res == NULL ? 0 : _m_res->_getObjectID());
		}
		break;
