int GuildManagerPOD::writeObjectMembers(ObjectOutputStream* stream) {
	int _count = ManagedServicePOD::writeObjectMembers(stream);

	uint32 _nameHashCode;
	int _offset;
	uint32 _totalSize;
	if (guildList) {
	_nameHashCode = 0xdec1a904; //GuildManager.guildList
	TypeInfo<uint32>::toBinaryStream(&_nameHashCode, stream);
	_offset = stream->getOffset();
	stream->writeInt(0);
	TypeInfo<DeltaSet<String, ManagedReference<GuildObjectPOD* > > >::toBinaryStream(&guildList.value(), stream);
	_totalSize = (uint32) (stream->getOffset() - (_offset + 4));
	stream->writeInt(_offset, _totalSize);
	_count++;
	}

	if (requiredMembers) {
	_nameHashCode = 0xe380d077; //GuildManager.requiredMembers
	TypeInfo<uint32>::toBinaryStream(&_nameHashCode, stream);
	_offset = stream->getOffset();
	stream->writeInt(0);
	TypeInfo<int >::toBinaryStream(&requiredMembers.value(), stream);
	_totalSize = (uint32) (stream->getOffset() - (_offset + 4));
	stream->writeInt(_offset, _totalSize);
	_count++;
	}

	if (maximumMembers) {
	_nameHashCode = 0x1c0d34c5; //GuildManager.maximumMembers
	TypeInfo<uint32>::toBinaryStream(&_nameHashCode, stream);
	_offset = stream->getOffset();
	stream->writeInt(0);
	TypeInfo<int >::toBinaryStream(&maximumMembers.value(), stream);
	_totalSize = (uint32) (stream->getOffset() - (_offset + 4));
	stream->writeInt(_offset, _totalSize);
	_count++;
	}

	if (guildUpdateInterval) {
	_nameHashCode = 0x8097075f; //GuildManager.guildUpdateInterval
	TypeInfo<uint32>::toBinaryStream(&_nameHashCode, stream);
	_offset = stream->getOffset();
	stream->writeInt(0);
	TypeInfo<int >::toBinaryStream(&guildUpdateInterval.value(), stream);
	_totalSize = (uint32) (stream->getOffset() - (_offset + 4));
	stream->writeInt(_offset, _totalSize);
	_count++;
	}


	return _count;
}

bool GuildManagerPOD::readObjectMember(ObjectInputStream* stream, const uint32& nameHashCode) {
	if (ManagedServicePOD::readObjectMember(stream, nameHashCode))
		return true;

	switch(nameHashCode) {
	case 0xdec1a904: //GuildManager.guildList
		{
			DeltaSet<String, ManagedReference<GuildObjectPOD* > > _mnguildList;
			TypeInfo<DeltaSet<String, ManagedReference<GuildObjectPOD* > > >::parseFromBinaryStream(&_mnguildList, stream);
			guildList = std::move(_mnguildList);
		}
		return true;

	case 0xe380d077: //GuildManager.requiredMembers
		{
			int _mnrequiredMembers;
			TypeInfo<int >::parseFromBinaryStream(&_mnrequiredMembers, stream);
			requiredMembers = std::move(_mnrequiredMembers);
		}
		return true;

	case 0x1c0d34c5: //GuildManager.maximumMembers
		{
			int _mnmaximumMembers;
			TypeInfo<int >::parseFromBinaryStream(&_mnmaximumMembers, stream);
			maximumMembers = std::move(_mnmaximumMembers);
		}
		return true;

	case 0x8097075f: //GuildManager.guildUpdateInterval
		{
			int _mnguildUpdateInterval;
			TypeInfo<int >::parseFromBinaryStream(&_mnguildUpdateInterval, stream);
			guildUpdateInterval = std::move(_mnguildUpdateInterval);
		}
		return true;

	}

	return false;
}

