GamblingTerminalAdapter::GamblingTerminalAdapter(GamblingTerminal* obj) : TerminalAdapter(obj) {
}

void GamblingTerminalAdapter::invokeMethod(uint32 methid, DistributedMethod* inv) {
	DOBMessage* resp = inv->getInvocationMessage();

	switch (methid) {
	case RPC_INITIALIZETRANSIENTMEMBERS__:
		{
			
			initializeTransientMembers();
			
		}
		break;
	case RPC_RESET__:
		{
			
			reset();
			
		}
		break;
	case RPC_GETFIRST__:
		{
			
			int _m_res = getFirst();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_GETSECOND__:
		{
			
			int _m_res = getSecond();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_GETTHIRD__:
		{
			
			int _m_res = getThird();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_SETFIRST__INT_:
		{
			int value = inv->getSignedIntParameter();
			
			setFirst(value);
			
		}
		break;
	case RPC_SETSECOND__INT_:
		{
			int value = inv->getSignedIntParameter();
			
			setSecond(value);
			
		}
		break;
	case RPC_SETTHIRD__INT_:
		{
			int value = inv->getSignedIntParameter();
			
			setThird(value);
			
		}
		break;
	case RPC_GETMINBET__:
		{
			
			int _m_res = getMinBet();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_GETMAXBET__:
		{
			
			int _m_res = getMaxBet();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_GETSTATE__:
		{
			
			int _m_res = getState();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_SETSTATE__INT_:
		{
			int value = inv->getSignedIntParameter();
			
			setState(value);
			
		}
		break;
	case RPC_GETMACHINETYPE__:
		{
			
			int _m_res = getMachineType();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_INCGAMECOUNT__:
		{
			
			incGameCount();
			
		}
		break;
	case RPC_GETGAMECOUNT__:
		{
			
			int _m_res = getGameCount();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_ISGONEEMPTY__:
		{
			
			bool _m_res = isGoneEmpty();
			resp->insertBoolean(_m_res);
		}
		break;
	case RPC_GETMACHINETYPETEXT__:
		{
			
			String _m_res = getMachineTypeText();
			resp->insertAscii(_m_res);
		}
		break;
	case RPC_GETTEXT__CREATUREOBJECT_:
		{
			CreatureObject* player = static_cast<CreatureObject*>(inv->getObjectParameter());
			
			String _m_res = getText(player);
			resp->insertAscii(_m_res);
		}
		break;
	case RPC_HANDLEOBJECTMENUSELECT__CREATUREOBJECT_BYTE_:
		{
			CreatureObject* player = static_cast<CreatureObject*>(inv->getObjectParameter());
			byte selectedID = inv->getByteParameter();
			
			int _m_res = handleObjectMenuSelect(player, selectedID);
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_SETGAMBLINGREGION__STRING_:
		{
			 String region; inv->getAsciiParameter(region);
			
			setGamblingRegion(region);
			
		}
		break;
	case RPC_GETGAMBLINGREGION__:
		{
			
			String _m_res = getGamblingRegion();
			resp->insertAscii(_m_res);
		}
		break;
	case RPC_GAMERUNNING__:
		{
			
			bool _m_res = gameRunning();
			resp->insertBoolean(_m_res);
		}
		break;
	case RPC_CHECKJOIN__CREATUREOBJECT_:
		{
			CreatureObject* player = static_cast<CreatureObject*>(inv->getObjectParameter());
			
			bool _m_res = checkJoin(player);
			resp->insertBoolean(_m_res);
		}
		break;
	case RPC_JOINTERMINAL__CREATUREOBJECT_:
		{
			CreatureObject* player = static_cast<CreatureObject*>(inv->getObjectParameter());
			
			joinTerminal(player);
			
		}
		break;
	case RPC_LEAVETERMINAL__CREATUREOBJECT_:
		{
			CreatureObject* player = static_cast<CreatureObject*>(inv->getObjectParameter());
			
			leaveTerminal(player);
			
		}
		break;
	case RPC_CLOSEMENU__CREATUREOBJECT_BOOL_:
		{
			CreatureObject* player = static_cast<CreatureObject*>(inv->getObjectParameter());
			bool payout = inv->getBooleanParameter();
			
			closeMenu(player, payout);
			
		}
		break;
	case RPC_CLOSEALLMENUS__:
		{
			
			closeAllMenus();
			
		}
		break;
	case RPC_STATUSUPDATE__CREATUREOBJECT_INT_:
		{
			CreatureObject* player = static_cast<CreatureObject*>(inv->getObjectParameter());
			int event = inv->getSignedIntParameter();
			
			statusUpdate(player, event);
			
		}
		break;
	case RPC_STATUSUPDATE__INT_:
		{
			int event = inv->getSignedIntParameter();
			
			statusUpdate(event);
			
		}
		break;
	default:
		TerminalAdapter::invokeMethod(methid, inv);
	}
}

