#include "engine/orb/messages/InvokeMethodMessage.h"


CraftingToolAdapter::CraftingToolAdapter(CraftingTool* obj) : ToolTangibleObjectAdapter(obj) {
}

void CraftingToolAdapter::invokeMethod(uint32 methid, DistributedMethod* inv) {
	DOBMessage* resp = inv->getInvocationMessage();

	switch (methid) {
	case RPC_INITIALIZETRANSIENTMEMBERS__:
		{
			
			initializeTransientMembers();
			
		}
		break;
	case RPC_HANDLEOBJECTMENUSELECT__CREATUREOBJECT_BYTE_:
		{
			CreatureObject* player = static_cast<CreatureObject*>(inv->getObjectParameter());
			byte selectedID = inv->getByteParameter();
			
			int _m_res = handleObjectMenuSelect(player, selectedID);
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_ISCRAFTINGTOOL__:
		{
			
			bool _m_res = isCraftingTool();
			resp->insertBoolean(_m_res);
		}
		break;
	case RPC_ISREADY__:
		{
			
			bool _m_res = isReady();
			resp->insertBoolean(_m_res);
		}
		break;
	case RPC_SETREADY__:
		{
			
			setReady();
			
		}
		break;
	case RPC_ISBUSY__:
		{
			
			bool _m_res = isBusy();
			resp->insertBoolean(_m_res);
		}
		break;
	case RPC_SETBUSY__:
		{
			
			setBusy();
			
		}
		break;
	case RPC_ISFINISHED__:
		{
			
			bool _m_res = isFinished();
			resp->insertBoolean(_m_res);
		}
		break;
	case RPC_SETFINISHED__:
		{
			
			setFinished();
			
		}
		break;
	case RPC_SENDTOOLSTARTFAILURE__CREATUREOBJECT_STRING_:
		{
			CreatureObject* player = static_cast<CreatureObject*>(inv->getObjectParameter());
			 String message; inv->getAsciiParameter(message);
			
			sendToolStartFailure(player, message);
			
		}
		break;
	case RPC_GETTOOLTYPE__:
		{
			
			int _m_res = getToolType();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_GETEFFECTIVENESS__:
		{
			
			float _m_res = getEffectiveness();
			resp->insertFloat(_m_res);
		}
		break;
	case RPC_GETCOMPLEXITYLEVEL__:
		{
			
			float _m_res = getComplexityLevel();
			resp->insertFloat(_m_res);
		}
		break;
	case RPC_GETPROTOTYPE__:
		{
			
			DistributedObject* _m_res = getPrototype();
			resp->insertLong(_m_res == NULL ? 0 : _m_res->_getObjectID());
		}
		break;
	case RPC_GETMANUFACTURESCHEMATIC__:
		{
			
			DistributedObject* _m_res = getManufactureSchematic();
			resp->insertLong(_m_res == NULL ? 0 : _m_res->_getObjectID());
		}
		break;
	case RPC_DISPERSEITEMS__:
		{
			
			disperseItems();
			
		}
		break;
	case RPC_GETFORCECRITICALASSEMBLY__:
		{
			
			int _m_res = getForceCriticalAssembly();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_GETFORCECRITICALEXPERIMENT__:
		{
			
			int _m_res = getForceCriticalExperiment();
			resp->insertSignedInt(_m_res);
		}
		break;
	case RPC_SETFORCECRITICALASSEMBLY__INT_:
		{
			int amount = inv->getSignedIntParameter();
			
			setForceCriticalAssembly(amount);
			
		}
		break;
	case RPC_SETFORCECRITICALEXPERIMENT__INT_:
		{
			int amount = inv->getSignedIntParameter();
			
			setForceCriticalExperiment(amount);
			
		}
		break;
	default:
		ToolTangibleObjectAdapter::invokeMethod(methid, inv);
	}
}

